mapper.select:
select count(*) from T_CHATROOM
where (U_ID=#{U_ID}
and CR_TITLE=#{F_ID}) or (U_ID=#{F_ID}
and CR_TITLE=#{U_ID})


select a.*
from(select * from T_CHATTING
where CR_SEQ=#{roomId}
order by CHAT_DT desc
)a where ROWNUM<=1

select CR_SEQ from T_CHATROOM
where (U_ID=#{U_ID}
and CR_TITLE=#{F_ID}) or (U_ID=#{F_ID}
and CR_TITLE=#{U_ID})
